INSERT INTO `depmain` (`id`, `name`, `text`, `img`) VALUES
(18, 'قسم هندسة التشييد والبناء', 'هو أحد أقسام الهندسة المدنية وهو التخطيط والإدارة وبناء المنشآت مثل الطرق السريعة والجسور والمطارات والسكك الحديدية والمباني والسدود والخزانات. ويتطلب بناء مثل هذه المشاريع الإلمام بمبادئ الهندسة، وإدارة الأعمال، والإجراءات،والاقتصاد،والسلوك الإنساني. إن من صلاحيات مهندسي التشييد تصميم المباني ذات الطابع المؤقت، وضمان ومراقبة الجودة، والتخطيط و مسح موقع التشييد، واختبار مواد التشييد في الموقع والرفع المساحي لمواقع التشييد، وتصميم خلطات الخرسانة، وتقدير التكاليف والتخطيط والجدولة، وهندسة السلامة، والمواد والمشتريات، واختيار المعدات، والتكاليف الهندسية ومراقبة الميزانية. تختلف هندسة التشييد عن إدارة التشييد من زاوية مستوى الرياضيات والعلوم والهندسة المستخدمة لتحليل مشاكل وعمليات التشييد.', '1631523797815.jpg'),
(19, 'هنسة الميكاترونيك', 'الميكاترونكس مصطلح يستعمل للدلالة على حقل هندسي واسع ومتشعب جداً، وهذا الحقل الهندسي يجمع بين الهندسة الميكانيكية، والهندسة الكهربائية، وهندسة الحاسوب وهندسة الإلكترونيات، ويتطور هذا التخصص بصورة مذهلة من يوم إلى آخر. هذا المجال من الهندسة يتضمن تصميم أي منتج (product) يعتمد عمله على دمج أنظمة ميكانيكية وإلكترونية، إذ يقوم بدور المنسق فيما بينها ووضع منظومة تحكم (control system) لها. ويُعرّف المعيار NF الفرنسي تحت الرقم E 01-010 الميكاترونكس على النحو التالي: &#34;النهج الذي يهدف إلى التكامل المستمر للميكانيكا والالكترونيات ونظرية التحكم، وعلوم الحاسب الآلي في تصميم المنتجات والصناعات التحويلية، من أجل تحسينه و/ أو تحسين وظائفه&#34;.', '5103703555938.jpg'),
(20, 'قسم هندسة الحاسب الآلى', 'مهندسو الحاسوب هم مهندسو إلكترونيات أساساً، ولديهم معلومات إضافية وتدريب وخبرة في مجال تصميم البرمجيات والعتاد الصلب للحاسوب، خصوصا في مجال تكامل البرمجيات مع العتاد. يشارك مهندسو الحاسوب في جميع مجالات الحوسبة من تصميم المعالجات الصغرية، والحواسيب الشخصية والحواسب الفائقة وحتى تصميم الدارات والدارات المتكاملة، بالإضافة لتكامل الأنظمة الحاسوبية مع أنواع أخرى من الأنظمة (مثل المركبات ذات المحركات والأنظمة الرقمية). [2]. يساهم مهندسو الحاسب أيضاً في كتابة الشفرات البرمجية البرمجيات المضمنة للمتحكمات الصغرية ذات الزمن الحقيقي، وتصميم الدارات المتكاملة VLSI، والعمل على الحساسات التماثلية، وتصميم أنظمة التشغيل وحتى لوحات الدارات والروبوتات.', '5371330200188.jpg'),
(21, 'الهندسة الصناعية', 'هو أحد فروع الهندسة ويهتم بالعلوم التي تخدم فن التصميم الميكانيكي وعمليات الإنتاج والتصنيع المختلفة، وكل ما يخدم ذلك في الأساس كالتخطيط والتصميم والتصنيع والتجميع والاختبار والفحص والتحليل والمعالجة والتطوير للحصول على أفضل قيمة مقابل أقل تكلفة، وهي تمسك هذه الشؤون من الجهة الاقتصادية ومن جهة التنظيم الصناعي. يعطى هذا التخصص الهندسي مسميات عدة كهندسة الدقة كما في اليابان وهندسة التصنيع أو الهندسة الصناعية', '8063455878836.jpg');

